export class Todo{constructor(t,i){this.id=t,this.title=i,this.isDone=!1,this.isEditing=!1}render(){const t=`\n        <li class="flex justify-between items-center bg-red-400 p-4 mb-2 text-left font-semibold hover:bg-red-300" id=${this.id}>\n            ${this.isEditing?`<input class="focus:outline-none bg-red-400 font-semibold mx-auto" type="text" value=${this.getTitle()}>`:`<p class="${this.isDone?"line-through":""}">${this.getTitle()}</p>`}\n                <div class="flex">\n                    <img src="/public/img/edit.svg" alt="" srcset="" class="w-4 cursor-pointer edit-icon">\n                    <img src="/public/img/delete.svg" alt="" srcset="" class="w-4 cursor-pointer ml-2 delete-icon">\n                </div>\n        </li>`;return t}toggleIsEditing(){this.isEditing=!this.isEditing}toggleDone(){this.isDone=!this.isDone}getIsEditing(){return this.isEditing}setTitle(t){this.title=t}getTitle(){return this.title.replace(/&/g,"&amp;").replace(/</g,"&lt;").replace(/>/g,"&gt;")}getId(){return this.id}getDone(){return this.isDone}}